"use strict";
/* Como criar variaveis */
/* Sintaxe: identificador(var (global), const(nao podemos reatribuir) e let( escopo local) nome da variavel */
let num = 1369;
console.log(num);
// Tipos basico
let firstName = "Leonel";
let age = 28;
console.log(firstName);
console.log(typeof firstName);
// Object
const myNumber = [1, 3, 4, 5];
console.log(myNumber);
console.log(myNumber.length);
console.log(firstName.toUpperCase());
myNumber.push(6);
console.log(myNumber);
